import React from 'react'
import { StyleSheet, View, Text, Image, TouchableWithoutFeedback } from 'react-native'
import { h, w } from './constants'
import { Ionicons } from '@expo/vector-icons'; 


const Header = ({title, onPress}) => {
    const {container, maintext} = styles
    return(
        <View style={[styles.box, styles.shadow2]}>
            <TouchableWithoutFeedback onPress={onPress}>
                <Ionicons name="ios-arrow-back" size={30} color="black" style={{ color: '#006AB3', paddingRight: 20, marginBottom: 8}}/>
            </TouchableWithoutFeedback>

            {/*<Image style={{width: 45, height: 45}} source={require('../assets/logo.jpeg')}/>*/}
            <Text style={maintext}>{title}</Text>
        </View>
    )
}

function elevationShadowStyle(elevation) {
    return {
      elevation,
      shadowColor: 'black',
      shadowOffset: { width: 0, height: 0.5 * elevation },
      shadowOpacity: 0.3,
      shadowRadius: 0.8 * elevation
    };
  }

const styles = StyleSheet.create({
    container: {
        backgroundColor: 'white',
        height: 80,
        width: w,
        paddingLeft: 10,
        paddingTop: 30,
        elevation: 10,
        position: 'relative',
        flexDirection: 'row',
        alignItems: 'center',
      },

    maintext: {
        fontSize: 30,
        color: 'gray',
        textAlignVertical: 'bottom',
        marginLeft: 10,
        fontFamily: 'roboto',
        textAlign: 'center',
      },

      shadow2: elevationShadowStyle(5),
      box: {
          flex: 1,
          borderRadius: 15,
          backgroundColor: 'white',
          width: w * 0.9, 
          marginTop: 10,
          flexDirection: 'column',
          paddingTop: 5,
          paddingBottom: 5,
          paddingRight: 19,
          paddingLeft: 15,
          alignSelf: 'center',
          minHeight: 50,
      },
})

export default Header
