import React from 'react';
import { TouchableOpacity, View, StyleSheet, Text, Image } from 'react-native';
import { h, w } from './constants';


const ListElement = ({onPress, title, source}) => {
    const { container, text } = styles

    return(
        <TouchableOpacity onPress={onPress}>
            <View style={[styles.box, styles.centerContent, styles.shadow2]}>
                <View style={{ height: 50, width: 50, borderRadius: 25,  borderWidth: 1, borderColor: 'rgb(125, 199, 28)', position: 'absolute', left: 5, top: 5}}>
                    <Image style={{ height: 50, width: 50, borderRadius: 25}} source={source}/>
                </View>
                <Text style={text} >{title}</Text>
            </View>
        </TouchableOpacity>
    );
}

function elevationShadowStyle(elevation) {
    return {
      elevation,
      shadowColor: 'black',
      shadowOffset: { width: 0, height: 0.5 * elevation },
      shadowOpacity: 0.3,
      shadowRadius: 0.8 * elevation
    };
  }

const styles = StyleSheet.create({
    text: {
        fontSize: 15,
        color: '#006AB3',
        fontFamily: 'roboto',
        textAlign: 'center',
        maxWidth: w * 0.85,
    },

    shadow2: elevationShadowStyle(10),
    box: {
        borderRadius: 30,
        backgroundColor: 'white',
        padding: 10,
        paddingLeft: 64,
        height: 60,
        width: w * 0.9,
        marginTop: 20,
        alignSelf: 'center'
    },
    centerContent: {
        justifyContent: 'space-around',
        alignItems: 'center'
    },
})

export default ListElement
